(function() {
  "use strict";
  require.config({
    deps: ["app"],
    paths: {
      jquery: "../vendor/jquery",
      backbone: "../vendor/backbone",
      underscore: "../vendor/underscore",
      marionette: "../vendor/marionette",
      validate: "../vendor/validate"
    },
    shim: {
      jquery: {
        exports: "$"
      },
      backbone: {
        deps: ["underscore", "jquery"],
        exports: "Backbone"
      },
      marionette: {
        deps: ["backbone"],
        exports: "Marionette"
      },
      validate: {
        deps: ["underscore", "backbone"],
        exports: "Validate"
      }
    }
  });

  require(["marionette", "router"], (function(Marionette, App) {
    return $(function() {
      App.start();
      return $(document).on("click", ".selectable", function(e) {
        return $("input[name=gift-search]").val($(e.target).html().trim());
      });
    });
  }));

}).call(this);
